function rot13(str) { // LBH QVQ VG!
  let result = [];
  for (let i in str) {
    let asc = str.charCodeAt(i);
    if (asc < 65 || asc > 90) {
      result.push(String.fromCharCode(asc));
    } else if (asc > 64 && asc < 78) {
      result.push(String.fromCharCode(asc + 13));
    } else {
      let difference = (90 - asc);
      let postasci = (13 - difference);
      result.push(String.fromCharCode(64 + postasci))
    }
  }
  console.log(result)
  return result.join('')

}

